  //setState를 호출할 때마다 변경된 state와 함께 render function 호출

class App extends React.Component {
  state ={
    isLoading: true,
    movies: []
  };

  componentDidMount() {
      setTimeout( () => {
        this.setState({isLoading: false});
      }, 6000);
  }


  render() {
    const {isLoading} = this.state;
    return <div>
      {isLoading ? "Loading.." : "We are ready"}
    </div>;
    }
